import React, {useState} from "react";
import { Switch, Button, TextField, FormControlLabel } from "@material-ui/core";

function FormularioCadastro() {
  const [nome, setNome] = useState("");
  const [sobreNome, setSobreNome] = useState("");
  const [cpf, setCpf] = useState("");
  const [promocoes, setPromocoes] = useState(true);
  const [novidades, setNovidades] = useState(true);

  return (
    <form onChange={(event => {
      event.preventDefault();
    })}>
      <TextField
      value={nome}
        onChange ={event => {
          setNome(event.target.value);
        }}
        id="nome"
        label="Nome"
        variant="outlined"
        margin="normal"
        fullWidth
      />
      <TextField
      value={sobreNome}
        onChange ={event => {
          setSobreNome(event.target.value)
        }}
        id="sobrenome"
        label="Sobrenome"
        variant="outlined"
        margin="normal"
        fullWidth
      />
      <TextField
        value={cpf}
        onChange ={event => {
          setCpf(event.target.value)
        }}
        id="cpf"
        label="CPF"
        variant="outlined"
        margin="normal"
        fullWidth
      />

      <FormControlLabel
        control={<Switch onChange={evento => {
          setPromocoes(evento.target.value)
        }} name="promocoes" defaultChecked={promocoes} color="primary" />}
        label="Promoções"
      />
      <FormControlLabel
        control={<Switch name="novidades" defaultChecked={novidades} color="primary" />}
        label="Novidades"
      />

      <Button type="submit" color="">Cadastrar</Button>
    </form>
  );
}

export default FormularioCadastro;
