import React, { useState } from "react";
import DadosPessoais from "./DadosPessoais";
import DadosUsuario from "./DadosUsuario";
import DadosEntrega from "./DadosEntrega";
import { Typography } from "@material-ui/core";

function FormularioCadastro({ aoEnviar, validarCpf }) {
  const [etapaAtual, setEtapaAtual] = useState(0);

  const formularios = [<DadosUsuario aoEnviar={proximo} />, <DadosPessoais aoEnviar={proximo} validarCpf={validarCpf} />, <DadosEntrega aoEnviar={aoEnviar}/>, <Typography>Erro ao selecionar formulário</Typography>  ]

  function proximo() {
    setEtapaAtual(etapaAtual+1);
    console.log(etapaAtual);
  }

  function formularioAtual(etapa) {
    switch (etapa) {
      case 0:
        
      case 1:
        return ;
      case 2:
        return ;
      default:
        return ;
    }
  }

  return (
    <>
      {formularioAtual(etapaAtual)}
    </>
  );
}

export default FormularioCadastro;
