import React, {useState} from "react";
import { Switch, Button, TextField, FormControlLabel } from "@material-ui/core";

function FormularioCadastro() {
  const [nome, setNome] = useState("Gabriel");
  const [sobreNome, setSobreNome] = useState("Cuin");

  return (
    <form onChange={(event => {
      event.preventDefault();
    })}>
      <TextField
      value={nome}
        onChange ={event => {
          let tmpNome = event.target.value;
          if (tmpNome.length >= 3) {
            tmpNome = tmpNome.substring(0, 3);
          }
          setNome(tmpNome)
        }}
        id="nome"
        label="Nome"
        variant="outlined"
        margin="normal"
        fullWidth
      />
      <TextField
      value={sobreNome}
        onChange ={event => {
          setSobreNome(event.target.value)
        }}
        id="sobrenome"
        label="Sobrenome"
        variant="outlined"
        margin="normal"
        fullWidth
      />
      <TextField
        id="cpf"
        label="CPF"
        variant="outlined"
        margin="normal"
        fullWidth
      />

      <FormControlLabel
        control={<Switch name="promocoes" defaultChecked color="primary" />}
        label="Promoções"
      />
      <FormControlLabel
        control={<Switch name="novidades" defaultChecked color="primary" />}
        label="Novidades"
      />

      <Button type="submit" color="">Cadastrar</Button>
    </form>
  );
}

export default FormularioCadastro;
